// Features Data

import { FaClipboardList, FaBell, FaShoppingCart, FaUtensils, FaMobileAlt, FaUserFriends } from 'react-icons/fa';

export const featuresData = [
  {
    icon: <FaClipboardList />,
    title: "Inventory Management",
    description: "Effortlessly track all your pantry items in one place. Categorize items by category, expiry date and quantity"
  },
  {
    icon: <FaBell />,
    title: "Expiry Date Reminders",
    description: "Receive timely notifications before items expire. Avoid food waste by using items before they go bad"
  },
  {
    icon: <FaShoppingCart />,
    title: "Custom Shopping lists",
    description: "Generate personalized shopping lists based on your pantry inventory"
  },
  {
    icon: <FaUtensils />,
    title: "Recipe Suggestions",
    description: "Get recipe ideas based on the items in your pantry. Maximize the use of available ingredients"
  },
  {
    icon: <FaMobileAlt />,
    title: "Multi-Device Sync",
    description: "Sync your pantry data across multiple devices. Access your pantry information anytime, anywhere."
  },
  {
    icon: <FaUserFriends />,
    title: "User-Friendly Interface",
    description: "Intuitive and easy-to-navigate design."
  }
];
