import React from 'react';
import Navbar from '../components/Navbar';
import landingPageImage from '../images/landing_page_image.png';

const LandingPage = () => {
  return (
    <div className="bg-white">
      {/* Navbar */}
      <Navbar isWaitlist={true} />

      {/* Hero Section */}
      <section className="px-20 py-16 flex items-center">
        <div className="w-1/2 flex flex-col items-start">
          {/* Header */}
          <h1 className="text-5xl font-bold mb-4 ">Effortlessly Track Your Pantry</h1>

          {/* Subheading */}
          <h2 className="text-3xl mb-4">
            <span className="text-blue-600">Organize</span> Your Kitchen with Ease.
          </h2>

          {/* Intro text */}
          <p className="mb-6">
            Keep track of your pantry items, reduce waste, and save money. Get timely reminders for expiring items and generate custom shopping lists to avoid duplicates and unnecessary purchases. Stay organized and make the most of your pantry with Pantry Pal.
          </p>

          {/* Get Started Button */}
          <button className="bg-blue-900 text-white px-6 py-3 rounded-lg text-lg font-semibold hover:bg-blue-800">
            Get Started
          </button>
        </div>

       {/* Image on the right */}
       <div className="w-2/5 flex justify-center">
          <img src={landingPageImage} alt="Pantry items" className="w-3/5 object-contain" />
        </div>
      </section>
    </div>
  );
};

export default LandingPage;
